import PouchDB from 'pouchdb';
import * as PQueue from 'p-queue';
import Session from 'api/session';

export default class Artwork {
  private queue: PQueue;

  private blobCache: {
    [id: string]: [number, Promise<string>]
  } = {};

  private artworks: PouchDB.Database<{}>;
  private static sInstance: Artwork;

  private PouchDB: PouchDB.Static;
  private constructor (pouch = PouchDB) {
    this.PouchDB = pouch;
    this.artworks = new pouch('artworks');
    this.queue = new PQueue({
      concurrency: 2,
    });
  }

  loadLargeCover (id: string, size = 64, target: HTMLImageElement) {
    return this.load(id, 'large', target);
  }

  loadSmallCover (id: string, size = 32, target: HTMLImageElement) {
    return this.load(id, 'small', target);
  }

  attach (blob: Blob, target: HTMLImageElement, oldSrc: string): Promise<Blob> {
    return new Promise((resolve) => {
      if (!target || oldSrc !== target.src) {
        return resolve(blob);
      }
      target.src = URL.createObjectURL(blob);
      target.addEventListener('load', () => {
        resolve(blob);
      });
    });
  }

  /**
   * 
   * @param docId the document id, starting with or without artowrks/
   * @param size the size, either large (300px) or small (64px)
   */
  load (docId: string, size: 'large' | 'small', target: HTMLImageElement): Promise<Blob> {
    const oldSrc = target ? target.src : null;
    return this.queue.add(() => {
      if (!docId.startsWith('artworks/')) {
        docId = 'artworks/' + docId;
      }
      return this.artworks.getAttachment(docId, size).catch((err) => {
        console.log('No attachment for: ' + docId);
        return fetch('/api/assets/no-album.jpg', {
          method: 'GET',
          headers: Session.headers()
        }).then((res) => {
          return res.blob();
        });
      }).then((res: Blob) => {
        return this.attach(res, target, oldSrc);
      });
    });
  }
  public static getInstance (pouch = PouchDB) {
    if (!Artwork.sInstance) {
      if (!pouch) {
        throw new Error("Missing pouch parameter for initialization");
      }
      Artwork.sInstance = new Artwork(pouch);
    }
    return Artwork.sInstance;
  }
  public static createInstance (pouch?: typeof PouchDB) {
    Artwork.sInstance = new Artwork(pouch);
  }
}

import * as jwt from 'jwt-decode';
import PouchDB from 'pouchdb';

class SessionManager {
  private storage: Storage;
  private tokenName = 'session_token';

  constructor(storage = window.localStorage) {
    this.storage = storage;
  }
  public getUser () {
    return this.decodeToken().user;
  }
  public getToken () {
    return this.storage.getItem(this.tokenName);
  }
  protected setToken (token: string) {
    this.storage.setItem(this.tokenName, token);
  }
  protected decodeToken (token = this.getToken()) {
    if (!token) return null;
    return jwt(token) as {
      exp: number,
      iat: number,
      iss: string,
      user: string,
      ok: boolean
    }
  }
  isSignedIn () {
    const session = this.decodeToken();
    if (!session) return false;
    if (session.exp > Date.now()) {
      return false;
    }
    if (!session.user || !session.ok) {
      return false;
    }
    return true;
  }
  getStatus (): Promise<{
    versions: {
      server: string,
      models: string,
    },
    user?: string
  }> {
    const init: RequestInit = {};
    if (this.isSignedIn()) {
      init.headers = this.headers();
    }
    return fetch('/api/status', init).then((res) => res.json());
  }
  signIn (username: string, password: string) {
    return fetch('/api/sessions', {
      method: 'POST',
      headers: new Headers({'content-type': 'application/json'}),
      body: JSON.stringify({username, password})
    }).then((res) => res.json()).then((res) => {
      
      this.setToken(res.token)
      
      return this.decodeToken(res.token);
    }).catch((err) => {
      return Promise.reject('Invalid username or password');
    });
  }
  fetch (input: RequestInfo, init: {
    method: string,
    body: any, 
    headers?: {
      [name: string]: string
    }
  } = {method: 'GET', body: {}}) {
    if (init.method === 'GET') {
      return fetch(input, this.init({
        method: 'GET',
        headers: new Headers(init.headers)
      }));
    }

    return fetch(input, this.init({
      ...init,
      headers: new Headers(init.headers)
    }));
  }
  headers (headers: any = {}) {
    if (headers instanceof Headers) {
      headers.set('Authorization', `Bearer ${this.getToken()}`);
      return headers;
    }
    return new Headers(Object.assign({}, headers, {
      Authorization: `Bearer ${this.getToken()}`
    }));
  }
  init (init: RequestInit = {}) {
    return Object.assign({}, init, {
      headers: this.headers(init.headers)
    });
  }
  logout () {
    this.storage.removeItem(this.tokenName);
  }
  destroy () {
    this.logout();
    const dbs =  [ 'artists', 'albums', 'tracks', 'artworks', 'files', 'trackers', 'libraries'];
    return Promise.all(dbs.map((db) => {
      const database = new PouchDB(db);
      return database.destroy();
    }));
  }
}

const instance = new SessionManager();

export default instance;

import {} from 'jest';
import { CompactdArtist } from 'api/models/CompactdArtist';
import { Status, FindMode } from 'api/models/CompactdModel';
import pouch from 'pouchdb';
import fetch from 'node-fetch';
import * as sinon from 'sinon';
import { Artist } from 'definitions';

pouch.plugin(require('pouchdb-adapter-memory'));
const PouchDB = pouch.defaults({adapter: 'memory'})

const BoundArtist = CompactdArtist.bind(null, PouchDB, fetch as any);
const sandbox = sinon.createSandbox();

const delay = (ms: number) => {
  return new Promise((resolve) => {
    setTimeout(resolve, ms);
  });
}

describe('CompactdArtist#constructor', () => {
  
  it ('creates a new database instance named `artist`', () => {
    expect.assertions(2);
    const pouch = sinon.spy(PouchDB);
    const artist = new CompactdArtist(pouch as any, null, 'foo');
    
    expect(pouch.calledOnce).toBe(true);
    expect(pouch.calledWithExactly('artists')).toBe(true);
  });

  it ('creates a barebone object when only id is passed', () => {
    const artist = new BoundArtist('foo');

    expect(artist.id).toBe('foo');
    expect(artist.status).toBe(Status.BAREBONE);
  });

  it ('creates a fetched object when id and name is passed', () => {
    const artist = new BoundArtist('foo', 'Foo');

    expect(artist.id).toBe('foo');
    expect(artist.name).toBe('Foo');
    expect(artist.status).toBe(Status.FETCHED);
  });

  it ('copies props from other object when cloned', () => {
    const artist = new BoundArtist('foo', 'Foo');
    const other = new BoundArtist(artist);

    expect(other.id).toBe('foo');
    expect(other.name).toBe('Foo');
    expect(other.status).toBe(Status.FETCHED);
  });

  it ('creates a fetched instance from props', () => {
    const artist = new BoundArtist({name: 'Foo', '_id': 'foo'});
    expect(artist.name).toBe('Foo');
    expect(artist.id).toBe('foo');
    expect(artist.status).toBe(Status.FETCHED);
  });
})

describe('CompactdArtist#props', () => {
  it ('returns an object with its props', () => {
    const artist = new BoundArtist('foo', 'bar');
    expect(artist.props).toEqual({_id: 'foo', name: 'bar'});
  });
});

describe('CompactdArtist#pull', () => {
  
  let getStub: sinon.SinonStub;
  beforeEach(() => {
    getStub = sandbox.stub(PouchDB.prototype, 'get').returns(Promise.resolve({
      name: 'Foobar',
      _id: 'foobar'
    }));
  });
  afterEach(() => {
    sandbox.restore();
  });
  it('calls PouchDB#get', async () => {
    expect.assertions(3);
    const artist = new CompactdArtist(PouchDB, null, 'foo', 'bar');
    await artist.pull();
    expect(getStub.calledOnce).toBeTruthy();
    expect(getStub.calledWithExactly("foo")).toBeTruthy();
    expect(artist.name).toBe("Foobar");
  });
  it('populates props', async () => {
    expect.assertions(1);
    const artist = new CompactdArtist(PouchDB, null, 'foo', 'bar');
    await artist.pull();
    expect(artist.name).toBe('Foobar');
  });
  it('sets its status to fetched', async () => {
    expect.assertions(1);
    const artist = new CompactdArtist(PouchDB, null, 'foo', 'bar');
    await artist.pull();
    
    expect(artist.status).toBe(Status.FETCHED);
  });
});

describe('CompactdArtist.findAll', () => {

  let allDocsStub: sinon.SinonStub;
  beforeEach(() => {
    allDocsStub = sandbox.stub(PouchDB.prototype, 'allDocs').resolves({
      rows: []
    });
  });
  afterEach(() => {
    sandbox.restore();
  });
  it ('calls PouchDB#allDocs with include_docs = false whith BAREBONE', async () => {
    expect.assertions(3);
    const res = await CompactdArtist.findAll(PouchDB, null, FindMode.BAREBONE);
    
    expect(res).toEqual([]);
    expect(allDocsStub.calledOnce).toBe(true);
    expect(allDocsStub.calledWith({include_docs: false})).toBe(true);
  });
  it ('calls PouchDB#allDocs with include_docs = true with PREFETCH', async () => {
    expect.assertions(3);
    const res = await CompactdArtist.findAll(PouchDB, null, FindMode.PREFETCH);
    
    expect(res).toEqual([]);
    expect(allDocsStub.calledOnce).toBe(true);
    expect(allDocsStub.calledWith({include_docs: true})).toBe(true);
  });
  it ('calls PouchDB#allDocs with startkey and endkey when key is passed', async () => {
    expect.assertions(3);
    const res = await CompactdArtist.findAll(PouchDB, null, FindMode.PREFETCH, 'foobar');
    
    expect(res).toEqual([]);
    expect(allDocsStub.calledOnce).toBe(true);
    expect(allDocsStub.calledWith({
      include_docs: true, startkey: 'foobar', endkey: 'foobar\uffff'})).toBe(true);
  });
  it ('maps each row with an artist', async () => {
    expect.assertions(5);

    allDocsStub.returns({
      rows: [{id: 'foo'}, {id: 'bar'}]
    });

    const res = await CompactdArtist.findAll(PouchDB, null, FindMode.PREFETCH, 'foobar');
    
    expect(res.length).toBe(2);
    expect(res[0].id).toBe('foo');
    expect(res[1].id).toBe('bar');
    expect(allDocsStub.calledOnce).toBe(true);
    expect(allDocsStub.calledWith({
      include_docs: true, startkey: 'foobar', endkey: 'foobar\uffff'})).toBe(true);
  });
});

describe('CompactdArtist#detachFeed', () => {
  it ('doesnt fire once called', async (done) => {
    expect.assertions(1);

    const artists = new PouchDB(CompactdArtist.DATABASE_NAME);
    const fz = await artists.put({
      _id: 'library/flatbush-zombies',
      name: 'Flatbush ZOMBiES'
    });
    const id = 'library/flatbush-zombies';
    const flatbush = new CompactdArtist(PouchDB, null, id);
    let rev: string = null;
    await flatbush.fetch();

    const stub = sinon.stub();

    const unbinder = flatbush.addOnPropsChangedListener(stub);
    const res = await artists.put({
      _rev: fz.rev,
      _id: id,
      name: 'Flatbush Zombies'
    });
    await delay(15);
    unbinder.unbind();

    const res2 = await artists.put({
      _rev: res.rev,
      _id: id,
      name: 'Flatbush ZOMBIES'
    });

    await artists.put({
      _rev: res2.rev,
      _id: id,
      name: 'Flatbush ZombiES'
    });

    expect(stub.calledOnce).toBe(true);

    done();
  });
});

describe ('CompactdArtist.create', () => {
  it('calls put with id and name', async () => {
    let putStub = sandbox.stub(PouchDB.prototype, 'put').resolves({});
    let getStub = sandbox.stub(PouchDB.prototype, 'get').resolves({
      _id: 'library/foo',
      name: 'foo'
    });
    
    const foo = await CompactdArtist.create(PouchDB, null, 'foo');

    expect(foo.name).toBe('foo');
    expect(foo.id).toBe('library/foo');

    expect(putStub.calledOnce).toBe(true);
    expect(putStub.calledWith({
      _id: 'library/foo',
      name: 'foo'
    }));

    sandbox.restore();
  });

  
  it('slugifies name', async () => {
    let putStub = sandbox.stub(PouchDB.prototype, 'put').resolves({});
    let getStub = sandbox.stub(PouchDB.prototype, 'get').resolves({
      _id: 'library/foo-heavy-heart-trees-and-salad',
      name: 'foo ❤️ trees & salad'
    });
    
    const foo = await CompactdArtist.create(PouchDB, null, 'foo');

    expect(foo.name).toBe('foo');
    expect(foo.id).toBe('library/foo');

    expect(putStub.calledOnce).toBe(true);
    expect(putStub.calledWith({
      _id: 'library/foo-heavy-heart-trees-and-salad',
      name: 'foo ❤️ trees & salad'
    }));

    sandbox.restore();
  });
});

describe ('CompactdArtist.byNameAscending', () => {
  it('sorts an array by name ascending', () => {
    const docs = ['foo', 'bar', 'zzrot', 'foobar'].map((name) => ({name}));

    const res = docs.sort(CompactdArtist.byNameAscending);

    expect(docs.map(({name}) => name)).toEqual([
      'bar', 'foo', 'foobar', 'zzrot'
    ]);
  });
});

describe ('CompactdArtist.toProps', () => {
  it ('maps models to props', () => {
    const props = {foo: 'bar'};
    const res = CompactdArtist.toProps()({props} as any);
    expect(props).toEqual(res);
  });
});

describe ('CompactdArtist.fromProps', () => {
  it ('maps flat props to models', () => {
    const props = {
      _id: 'foo',
      name: 'bar'
    };

    const foo = CompactdArtist.fromProps(PouchDB, null)(props);

    expect(foo.id).toBe('foo');
    expect(foo.name).toBe('bar');
    expect(foo).toBeInstanceOf(CompactdArtist);
  });
  it ('maps nested props to models', () => {
    const props = {
      _id: 'foo',
      name: 'bar'
    };

    const foo = CompactdArtist.fromProps(PouchDB, null)({
      doc: props, id: 'foo'});

    expect(foo.id).toBe('foo');
    expect(foo.name).toBe('bar');
    expect(foo).toBeInstanceOf(CompactdArtist);
  });
  it ('maps id to models', () => {

    const foo = CompactdArtist.fromProps(PouchDB, null)({id: 'foo'});

    expect(foo.id).toBe('foo');
    expect(foo.name).toBe(null);
    expect(foo).toBeInstanceOf(CompactdArtist);
  });
});

import { CompactdModel, Status, FindMode } from "api/models/CompactdModel";
import { Artist, artistURI, mapArtistToParams } from "compactd-models";
import Map from 'models/Map';
import Artwork from "api/Artwork";

export class CompactdArtist extends CompactdModel<Artist> {
  public static readonly DATABASE_NAME = "artists";

  private _changes: PouchDB.Core.Changes<Artist>;
  private _name: string;

  /**
   * Copy constructor
   * @param pouchdb the pouchdb constructor
   * @param _fetch the fetch fucntion
   * @param model the otehr model
   */
  public constructor (pouchdb: PouchDB.Static, _fetch: typeof fetch, model: CompactdArtist)
  /**
   * Constructe from props object
   * @param pouchdb 
   * @param _fetch 
   * @param props
   */
  public constructor (pouchdb: PouchDB.Static, _fetch: typeof fetch, props: Artist)
  public constructor (pouchdb: PouchDB.Static, _fetch: typeof fetch, id: string, name?: string)
  public constructor (pouchdb: PouchDB.Static, _fetch: typeof fetch, id: string | Artist | CompactdArtist, name?: string) {
    if (typeof id === 'string') {
      super(pouchdb, _fetch, CompactdArtist.DATABASE_NAME, id, !name ?  Status.BAREBONE : Status.FETCHED);
      this._name = name;
    } else if (id instanceof CompactdArtist) {
      super(pouchdb, _fetch, CompactdArtist.DATABASE_NAME, id._id, id._status);
      this._name = id._name;
    } else if (typeof id === 'object') {
      super(pouchdb, _fetch, CompactdArtist.DATABASE_NAME, id._id,  Status.FETCHED);
      this._name = id.name;
    }
  }
  public getRevision () {
    return this.database.get(this._id).then((doc) => doc._rev);
  }
  /**
   * Artist name
   */
  public get name () {
    return this._name;
  }

  public get props (): Artist {
    return {
      _id: this._id,
      name: this._name
    }
  }

  public async pull(): Promise<void> {
    const props = await this.database.get(this._id);
    
    this._name = props.name;
  }

  public loadArtworkInto (target: HTMLImageElement) {
    if (target.getBoundingClientRect().width <= 64) {
      return Artwork.getInstance(this.pouchdb).load(this._id, 'small', target);
    }
    return Artwork.getInstance(this.pouchdb).load(this._id, 'large', target);
  }

  /**
   * Find all artists
   * @param pouchDB the pouchDB constructor to use
   * @param f the fetch function to use
   * @param mode the find mode
   * @param key (optionnal) the starting key
   */
  public static async findAll (pouchDB: PouchDB.Static, f: typeof fetch, mode: FindMode, key?: string): Promise<CompactdArtist[]> {
    const artists = new pouchDB<Artist>(CompactdArtist.DATABASE_NAME);

    const items = await artists.allDocs(Object.assign({
      include_docs: mode === FindMode.PREFETCH
    }, key ? {
      startkey: key,
      endkey: key + '\uffff'
    } : {}));

    return items.rows.map(CompactdArtist.fromProps(pouchDB, f));
  }
  
  protected attachFeed(): void {
    if (this._changes) {
      return;
    }
    this._changes = this.database.changes({
      since: 'now',
      live: true,
      include_docs: true,
      doc_ids: [this._id]
    }).on('change', ({doc, deleted, id}) => {
      if (deleted) {
        this._status = Status.DELETED;
        this.fireOnDelete(id);
        return;
      }

      if (doc.name !== this._name) {
        const props = this.props;
        this._name = doc.name;
        this.fireOnPropsChanged('name', doc, props);
        return;
      }
      
      console.log('Unable to see change', arguments);
    })
  }

  protected detachFeed(): void {
    if (this._changes) {
      this._changes.cancel();
    }
  }

  public delete(): Promise<void> {
    throw new Error("Method not implemented.");
  }

  /**
   * Creates a new database entity
   * @param pouch the database to use
   * @param _fetch the fetch function
   * @param name the name of the artist, unslugified
   */
  public static create (pouch: PouchDB.Static, _fetch: typeof fetch, name: string) {
    const db = new pouch(CompactdArtist.DATABASE_NAME);

    const _id = artistURI(mapArtistToParams({name}));

    return db.put({_id, name}).then(() => {
      return new CompactdArtist(pouch, _fetch, _id, name);
    });
  }

  /**
   * helper function to sort by name
   * @param a 
   * @param b 
   */
  public static byNameAscending (a: {name: string}, b: {name: string}) {
    if(a.name < b.name) return -1;
    if(a.name > b.name) return 1;
    return 0;
  }

  /**
   * Helper to map artists to their props
   */
  public static toProps () {
    return (item: CompactdArtist) => {
      return item.props;
    }
  }

  /**
   * Helper to map props to artist
   * @param pouch pouchDB constructor
   * @param _fetch fethc function
   */
  public static fromProps (pouch: PouchDB.Static, _fetch: typeof fetch) {
    return (props: Artist | {doc?: Artist, id: string}) => {
      if ((props as any).id) {
        return new CompactdArtist(pouch, _fetch, (props as any).id, (props as any).doc ? (props as any).doc.name : null);
      }
      return new CompactdArtist(pouch, _fetch, props as Artist);
    }
  }
}

